import React from "react";
import styled from "styled-components";

import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles({
  root: {
    maxWidth: 405,
  },
  media: {
    height: 180,
  },
  paragraph: {
    height: 90,
  },
});

function Project({ title, image, cloneURL, git, text }) {
  const classes = useStyles();
  const linkToSite = () =>{
    window.open(cloneURL)
  }
  const linkToGit = () =>{
    window.open(git)
  }

  return (
    <ProjectContainer>
      <Card className={classes.root} onClick={linkToSite}>
        <CardActionArea>
          <CardMedia className={classes.media} image={image} />
          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              {title}
            </Typography>
            <Typography
              variant="body2"
              component="p"
              className={classes.paragraph}
            >
              {text}
            </Typography>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button size="small" color="primary" onClick={linkToSite}>
              View More
          </Button>
          <Button size="small" color="primary" onClick={linkToGit}>
            Github
          </Button>
        </CardActions>
      </Card>
    </ProjectContainer>
  );
}

export default Project;

const ProjectContainer = styled.div`
  > .MuiPaper-root {
    background-image: linear-gradient(315deg, #8899A6 0%, #b3b3b3 74%);
    /* background-image: linear-gradient(315deg, #b3b3b3 0%, #ffffff 74%); */
    &:hover {
      border: .5px solid #b3b3b3;
      margin: 1px;
      /* transform: skew(20deg); */
      /* transform: scale(1.1); */
    }
    /* TARGETS CARD IMAGE */
    /* >.MuiButtonBase-root > .MuiCardMedia-root{
      object-fit: contain;
    } */
  }
`;
