import React from "react";
import styled from "styled-components";
import Typography from "@material-ui/core/Typography";
import image from "../img/caliLove.jpg";
import waterfall from "../img/waterfall.jpg";
import { Grid } from "@material-ui/core";

function About() {
  return (
    <AboutContainer id="about" style={{ backgroundImage: `url(${waterfall})` }}>
      <Grid container direction="row" justify="center" alignItems="center">
        <Grid item xs={12} sm={6} md={6} lg={3}>
          <AboutLeft>
            <img src={image} alt="" />
          </AboutLeft>
        </Grid>
        <Grid item xs={12} sm={6} md={3} lg={3}>
          <AboutRight>
            <Typography variant="h3" align="center">
              Hello world, <br/>I'm Sam.
            </Typography>
            <hr/>
            <Typography variant="body1" paragraph={true} align="center">
              I'm a self-taught developer. I was an Economics graduate with some
              background in mathematics and finance. I've worked several full
              time jobs through university so I'm familiar with managing
              multiple tasks and time management/planning accordingly. My
              current skillset is mainly focused on frontend development. I've
              always had an entrepreneurial spirit, extemely passionate for all
              technologies, and always willing to contribute in anyway I can.
              Please feel free to send a text or email, lets chat. Contact info
              is below.
            </Typography>
          </AboutRight>
        </Grid>
      </Grid>
    </AboutContainer>
  );
}

export default About;

const AboutContainer = styled.div`
  padding-top: 10%;
  padding-bottom: 10%;
  padding-left: 5%;
  padding-right: 5%;
  background-color: #000000;
  background-image: linear-gradient(315deg, #b3b3b3 0%, #ffffff 74%);
  background-repeat: no-repeat;
  background-size: cover;
  background-position: top right;
  opacity: 0.9;
  min-height:44vh;
`;

const AboutLeft = styled.div`
  display: grid;
  place-items: center;
  > img {
    object-fit: contain;
    max-width: 300px;
    border-radius: 1999px;
  }
`;
const AboutRight = styled.div`
  > .MuiTypography-root {
    color: white;
    > h4 {
      font-weight: 200;
    }
    > body2{
      font-weight:900;
    }
  }
`;
