import { Grid, Typography } from "@material-ui/core";
import React from "react";
import styled from "styled-components";
import uuid from "react-uuid";

// LOCAL IMG FILES
import imgAirbnb from "../img/airbnb.png";
import imgHulu from "../img/hulu.png";
import imgSpotify from "../img/spotify.png";
import imgCovidTracker from "../img/covidtracker.png";
import imgGoogle from "../img/google.png";
import imgWhatsapp from "../img/whatsapp.png";
import imgMizumi from "../img/calculator.png";
import imgTiktok from "../img/tiktok.png";
import imgSlack from "../img/slack.png";
import Project from "./Project";

function Projects() {
  return (
    <ProjectsContainer id="projects">
      <Grid
        container
        spacing={2}
        direction="row"
        justify="center"
        alignItems="center"
      >
        <Grid item xs={12} lg={12}>
          <Typography variant="h5" paragraph={true} align="center">
            Please feel free to sample any projects.
          </Typography>
        </Grid>

        {projects.map(({ title, text, image, cloneURL, git }) => (
          <Grid key={uuid()} item xs={12} sm={6} md={4} lg={4} xl={3}>
            <Project
              title={title}
              text={text}
              image={image}
              cloneURL={cloneURL}
              git={git}
            />
          </Grid>
        ))}
      </Grid>
    </ProjectsContainer>
  );
}

export default Projects;

const ProjectsContainer = styled.div`
  display: grid;
  place-items: center;
  padding-top: 8%;
  padding-bottom: 10%;
  padding-left: 10%;
  padding-right: 10%;
  background-image: linear-gradient(315deg, #8899a6 0%, #b3b3b3 74%);
  > .MuiGrid-root > .MuiGrid-root > .MuiTypography-root {
    color: mix(#fff, #e91e63, 85%);
    font-size: 2em;
    font-weight: 900;
    border-bottom: 2px solid whitesmoke;
    text-shadow: 1px 1px 0 grey, 1px 2px 0 grey, 1px 3px 0 grey, 1px 4px 0 grey,
      1px 5px 0 grey, 1px 6px 0 grey, 1px 7px 0 grey, 1px 8px 0 grey,
      5px 13px 15px black;
  }
`;

const projects = [
  {
    title: "Google Clone",
    text:
      "Used Google Custom Search JSON API to obtain search results. I used Reactjs for the frontend with the context API, @material-ui, flexbox, and firebase for hosting.",
    image: imgGoogle,
    cloneURL: "https://clone-56e6c.web.app/",
    git: "https://github.com/squireaintready/google-clone/tree/gh-pages",
  },
  {
    title: "Hulu Clone",
    text:
      "Using the movie database API (TMDB) to sort movie objects, this hulu clone returns results based on top categories/genres. I used Reactjs for the frontend, using tools such as hooks",
    react: "router, material UI, and flexbox. Deployed on firebase hosting.",
    image: imgHulu,
    cloneURL: "https://hulu-clone-65a1a.web.app/",
    git: "",
  },
  {
    title: "Global Covid-19 Tracker",
    text:
      "Disease.sh API for retrieving/updating live data such as the number of new cases, recovered, and deaths. Developed with Reactjs, Leaflet API for maps, Chart.js for plotting significant data points, flexbox, and hosting done on firebase.",
    image: imgCovidTracker,
    cloneURL: '"https://covid-19-tracker-31079.web.app/',
    git: "https://github.com/squireaintready/covid-tracker/tree/gh-pages",
  },
  {
    title: "AirBnb Clone",
    text:
      "Datepicker API for user to create travel/search window. Used Reactjs for the front end, functional components, useEffect and useState hooks, react-router, flexbox, and @material-UI. Deployed on Firebase hosting.",
    image: imgAirbnb,
    cloneURL: '"https://airbnb-clone-9d9bd.web.app/',
    git: "https://github.com/squireaintready/airbnb-clone",
  },
  {
    title: "Whatsapp Clone",
    text:
      "Used Firebase for hosting/google log in authentication. Cloud Firestore for storing messages into a live database. Skills used includes hooks, react-router, Context API, flexbox, and @material UI.",
    image: imgWhatsapp,
    cloneURL: "https://whatsapp-clone-62615.web.app/",
    git: "",
  },
  {
    title: "Spotify Clone",
    text:
      "Used Spotify's amazing API for user authentication and pulling users playlist. Reactjs for the front end, as well as @material-ui for components and icons, hooks, flexbox, and context API for data layer. Deployed using on firebase hosting.",
    image: imgSpotify,
    cloneURL: '"https://spotify-clone-3a930.web.app/',
    git: "https://github.com/squireaintready/spotify-clone/tree/gh-pages",
  },
  {
    title: "Mizumi Calculator",
    text:
      "My first project was developed for a restaurant I worked at during university. It was a large buffet with over 20 servers. I developed a tip calculator to aid employees with distribution using an in-house algorithm. Built fully with Javascript and Jquery to target DOM elements.",
    image: imgMizumi,
    cloneURL: '"https://squireaintready.github.io/advancedTip/',
    git: "https://github.com/squireaintready/serversCalculator",
  },
  {
    title: "Slack Clone",
    text:
      "Frontend React JS: Material UI, Flexbox, Context API, Google User authentication, real time database with firestore and hosting with firebase",
    image: imgSlack,
    cloneURL: "https://slack-clone-b635a.web.app/",
    git: "https://github.com/squireaintready/slack-clone/tree/gh-pages",
  },
  {
    title: "Tik-tok Clone",
    text:
      "Frontend React JS: Hooks, Flexbox, Material UI. Firebase database and hosting.",
    image: imgTiktok,
    cloneURL: "https://tiktok-clone-f9e09.web.app/",
    git: "",
  },
];
